/*
create database aoc2015
go
use aoc2015
go

create procedure day3_update @op varchar(1), @x int output, @y int output
as
begin
	select @x = case @op
				when '>' then @x + 1
				when '<' then @x - 1
				else @x
				end,
		   @y = case @op
				when '^' then @y + 1
				when 'v' then @y - 1
				else @y
				end
	if exists (select 1 from #data where x = @x and y = @y)
		update #data set c = c + 1 where x = @x and y = @y
	else 
		insert into #data values(@x, @y, 1)
end
-- */
go



